import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Box, Typography } from '@mui/material';

const AddTask = () => {
  const [task, setTask] = useState({
    title: '',
    description: '',
    deadline: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTask({
      ...task,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post(`${process.env.REACT_APP_API_URL}/tasks`, task)
      .then((response) => {
        alert('Task added successfully');
        setTask({ title: '', description: '', deadline: '' });
      })
      .catch((error) => console.error('Error adding task:', error));
  };

  return (
    <Box sx={{ marginBottom: 3 }}>
      <Typography variant="h5" gutterBottom>
        Add New Task
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Task Title"
          name="title"
          value={task.title}
          onChange={handleChange}
          required
          margin="normal"
        />
        <TextField
          fullWidth
          label="Description"
          name="description"
          value={task.description}
          onChange={handleChange}
          margin="normal"
          multiline
          rows={4}
        />
        <TextField
          fullWidth
          label="Deadline"
          name="deadline"
          type="date"
          value={task.deadline}
          onChange={handleChange}
          InputLabelProps={{
            shrink: true,
          }}
          margin="normal"
        />
        <Button
          variant="contained"
          type="submit"
          color="primary"
          sx={{ marginTop: 2 }}
        >
          Add Task
        </Button>
      </form>
    </Box>
  );
};

export default AddTask;
