import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Typography } from '@mui/material';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [open, setOpen] = useState(false); // State for dialog visibility
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    deadline: '',
    completed: false,
  });

  // Fetch tasks on component mount
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/tasks`)
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    setNewTask({ ...newTask, [e.target.name]: e.target.value });
  };

  // Handle opening the dialog
  const handleOpenDialog = () => {
    setOpen(true);
  };

  // Handle closing the dialog
  const handleCloseDialog = () => {
    setOpen(false);
    setNewTask({
      title: '',
      description: '',
      deadline: '',
      completed: false,
    }); // Reset the form when dialog is closed
  };

  // Handle adding a new task
  const handleAddTask = (e) => {
    e.preventDefault();

    // Ensure title and deadline are provided
    if (!newTask.title || !newTask.deadline) {
      alert('Title and Deadline are required');
      return;
    }

    axios
      .post(`${process.env.REACT_APP_API_URL}/tasks`, newTask)
      .then((response) => {
        setTasks([...tasks, response.data]);
        handleCloseDialog(); // Close the dialog after adding the task
      })
      .catch((error) => {
        console.error('Error adding task:', error);
        alert('Failed to add task');
      });
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography variant="h4" gutterBottom>
        To-Do List
      </Typography>

      {/* Button to open the "Add Task" dialog */}
      <Button variant="contained" color="primary" onClick={handleOpenDialog}>
        Add Task
      </Button>

      {/* Dialog for adding a new task */}
      <Dialog open={open} onClose={handleCloseDialog}>
        <DialogTitle>Add New Task</DialogTitle>
        <DialogContent>
          <form onSubmit={handleAddTask}>
            <TextField
              label="Title"
              name="title"
              value={newTask.title}
              onChange={handleInputChange}
              fullWidth
              required
              margin="normal"
            />
            <TextField
              label="Description"
              name="description"
              value={newTask.description}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Deadline"
              name="deadline"
              type="date"
              value={newTask.deadline}
              onChange={handleInputChange}
              fullWidth
              required
              margin="normal"
            />
            <DialogActions>
              <Button onClick={handleCloseDialog} color="secondary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                Add Task
              </Button>
            </DialogActions>
          </form>
        </DialogContent>
      </Dialog>

      {/* List of tasks */}
      <Box>
        {tasks.length === 0 ? (
          <Typography>No tasks available</Typography>
        ) : (
          tasks.map((task) => (
            <Box key={task._id} sx={{ margin: '10px 0', display: 'flex', justifyContent: 'space-between' }}>
              <Typography variant="body1" sx={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
                {task.title} - {task.deadline}
              </Typography>
              <div>
                <Button variant="contained" color={task.completed ? 'secondary' : 'primary'}>
                  {task.completed ? 'Undo' : 'Complete'}
                </Button>
                <Button variant="outlined" color="error">
                  Delete
                </Button>
              </div>
            </Box>
          ))
        )}
      </Box>
    </Box>
  );
};

export default TaskList;
