import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Typography, Card, CardContent, CardActions, Checkbox, FormControlLabel } from '@mui/material';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [open, setOpen] = useState(false); // State for dialog visibility
  const [editMode, setEditMode] = useState(false); // Flag to check if it's an edit mode
  const [isDeadlineChanged, setIsDeadlineChanged] = useState(false);

  const [currentTask, setCurrentTask] = useState({
    _id: '',
    title: '',
    description: '',
    deadline: '',
    completed: false,
  });

  // Validation errors
  const [errors, setErrors] = useState({
    title: '',
    description: '',
    deadline: '',
  });

  // State for delete confirmation dialog
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState(null);

  // Fetch tasks on component mount
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/tasks`)
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
  
    // If the deadline field is being edited, set isDeadlineChanged to true
    if (name === 'deadline') {
      setIsDeadlineChanged(true);
    }
  
    setCurrentTask({
      ...currentTask,
      [name]: value,
    });
  };
  

  // Handle Completed checkbox change (Only in Edit mode)
  const handleCompletedChange = (e) => {
    setCurrentTask({ ...currentTask, completed: e.target.checked });
  };

  // Validate the title, description, and deadline
  const validateFields = () => {
    const { title, description, deadline } = currentTask;
    let formValid = true;
    const newErrors = { title: '', description: '', deadline: '' };
  
    // Title validation
    if (!title) {
      newErrors.title = 'Title is required';
      formValid = false;
    } else if (title.length < 5) {
      newErrors.title = 'Title must be at least 5 characters';
      formValid = false;
    } else if (title.length > 25) {
      newErrors.title = 'Title must not exceed 25 characters';
      formValid = false;
    } else if (title.trim() !== title) {
      newErrors.title = 'Title must not start or end with spaces';
      formValid = false;
    }
  
    // Deadline validation (should be in the future)
    if (!deadline) {
      newErrors.deadline = 'Deadline is required';
      formValid = false;
    } else {
      const currentDate = new Date();
      const deadlineDate = new Date(deadline);
      if (deadlineDate <= currentDate) {
        newErrors.deadline = 'Deadline must be in the future';
        formValid = false;
      }
    }
  
    setErrors(newErrors);
    return formValid;
  };
  
  

  // Handle opening the dialog for adding a new task
  const handleOpenDialog = () => {
    setOpen(true);
    setEditMode(false); // Open in add mode
    setCurrentTask({
      _id: '',
      title: '',
      description: '',
      deadline: '',
      completed: false,
    });
    setErrors({ title: '', description: '', deadline: '' }); // Reset errors
  };

  // Handle opening the dialog for editing a task
  const handleOpenEditDialog = (task) => {
    setOpen(true);
    setEditMode(true); // Open in edit mode
  
    setCurrentTask({
      _id: task._id,
      title: task.title,
      description: task.description,
      deadline: task.deadline ? new Date(task.deadline).toISOString().slice(0, 16) : '', // Pre-fill deadline (YYYY-MM-DDTHH:mm)
      completed: task.completed,
    });
  
    setIsDeadlineChanged(false); // Reset the flag
    setErrors({ title: '', description: '', deadline: '' }); // Reset errors
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString(); // Formats the date and time to a readable string (e.g., "MM/DD/YYYY, HH:mm")
  };
  
  
  

  // Handle closing the dialog
  const handleCloseDialog = () => {
    setOpen(false);
    setErrors({ title: '', description: '', deadline: '' }); // Reset errors
  };

  // Handle adding or editing a task
  const handleSubmit = (e) => {
    e.preventDefault();
  
    // Only validate if deadline has been changed
    if (isDeadlineChanged) {
      const currentDate = new Date();
      const taskDeadline = new Date(currentTask.deadline);
  
      // If the deadline is in the past, show an error and prevent saving
      if (taskDeadline < currentDate) {
        setErrors({
          ...errors,
          deadline: 'Deadline must be in the future',
        });
        return; // Prevent form submission
      }
    }
  
    // Proceed with saving the task if there are no errors
    if (validateFields()) {
      const taskData = {
        title: currentTask.title,
        description: currentTask.description,
        deadline: currentTask.deadline,
        completed: currentTask.completed, // Ensure the completed status is included
      };
  
      if (editMode) {
        // Update task
        axios
          .put(`${process.env.REACT_APP_API_URL}/tasks/${currentTask._id}`, taskData)
          .then((response) => {
            setTasks(
              tasks.map((task) =>
                task._id === currentTask._id ? { ...task, ...response.data } : task
              )
            );
            handleCloseDialog();
          })
          .catch((error) => {
            console.error('Error editing task:', error);
            alert('Failed to edit task');
          });
      } else {
        // Add new task
        axios
          .post(`${process.env.REACT_APP_API_URL}/tasks`, taskData)
          .then((response) => {
            setTasks([...tasks, response.data]);
            handleCloseDialog();
          })
          .catch((error) => {
            console.error('Error adding task:', error);
            alert('Failed to add task');
          });
      }
    }
  };
  
  
  
  

  // Handle task deletion
  const handleDelete = (taskId) => {
    axios
      .delete(`${process.env.REACT_APP_API_URL}/tasks/${taskId}`)
      .then(() => {
        setTasks(tasks.filter((task) => task._id !== taskId));
        setDeleteDialogOpen(false); // Close the delete confirmation dialog
      })
      .catch((error) => {
        console.error('Error deleting task:', error);
        alert('Failed to delete task');
        setDeleteDialogOpen(false); // Close the dialog in case of error
      });
  };

  // Open delete confirmation dialog
  const openDeleteDialog = (task) => {
    setTaskToDelete(task);
    setDeleteDialogOpen(true);
  };

  // Close delete confirmation dialog
  const closeDeleteDialog = () => {
    setDeleteDialogOpen(false);
    setTaskToDelete(null); // Reset task to delete
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography variant="h4" gutterBottom>
        To-Do List
      </Typography>

      {/* Button to open the "Add Task" dialog */}
      <Button variant="contained" color="primary" onClick={handleOpenDialog}>
        Add Task
      </Button>

      {/* Task Cards */}
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginTop: '20px' }}>
        {tasks.length === 0 ? (
          <Typography>No tasks available</Typography>
        ) : (
          tasks.map((task) => (
            <Card key={task._id} sx={{ width: '300px', boxShadow: 3 }}>
              <CardContent>
                <Typography variant="h6" style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
                  {task.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {task.description}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {formatDate(task.deadline)} {/* Format the deadline to display */}
                </Typography>
                <Typography variant="body2" color={task.completed ? 'green' : 'red'}>
                  {task.completed ? 'Completed' : 'Pending'}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary" onClick={() => handleOpenEditDialog(task)}>
                  Edit
                </Button>
                <Button size="small" color="error" onClick={() => openDeleteDialog(task)}>
                  Delete
                </Button>
              </CardActions>
            </Card>
          ))
        )}
      </Box>

      {/* Dialog for adding or editing a task */}
      <Dialog open={open} onClose={handleCloseDialog}>
        <DialogTitle>{editMode ? 'Edit Task' : 'Add New Task'}</DialogTitle>
        <DialogContent>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Title"
              name="title"
              value={currentTask.title}
              onChange={handleInputChange}
              fullWidth
              required
              margin="normal"
              error={!!errors.title}
              helperText={errors.title}
            />
            <TextField
              label="Description"
              name="description"
              value={currentTask.description}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
              error={!!errors.description}
              helperText={errors.description}
            />
          <TextField
            label="Deadline"
            name="deadline"
            type="datetime-local"
            value={currentTask.deadline} // Ensure this value is in YYYY-MM-DDTHH:mm format
            onChange={handleInputChange}
            fullWidth
            required
            margin="normal"
            InputLabelProps={{
              shrink: true,  // Ensures label is always above the input field
            }}
            error={!!errors.deadline}
            helperText={errors.deadline}
          />



            {/* Only show Completed checkbox in Edit mode */}
            {editMode && (
              <FormControlLabel
                control={
                  <Checkbox
                    checked={currentTask.completed}
                    onChange={handleCompletedChange}
                    name="completed"
                    color="primary"
                  />
                }
                label="Completed"
              />
            )}
            <DialogActions>
              <Button onClick={handleCloseDialog} color="secondary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                {editMode ? 'Update Task' : 'Add Task'}
              </Button>
            </DialogActions>
          </form>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={deleteDialogOpen} onClose={closeDeleteDialog}>
        <DialogTitle>Confirm Deletion</DialogTitle>
        <DialogContent>
          <Typography>Are you sure you want to delete this task?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeDeleteDialog} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={() => {
              if (taskToDelete) handleDelete(taskToDelete._id);
            }}
            color="error"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TaskList;
