import React, { useState } from 'react';
import { Tooltip } from 'react-tooltip'; // Assuming you're using 'react-tooltip' for tooltips

const OnboardingGuide = () => {
  const [showGuide, setShowGuide] = useState(true); // State to control whether the guide is shown

  // Close the guide
  const handleCloseGuide = () => {
    setShowGuide(false);
  };

  return (
    <>
      {showGuide && (
        <div className="onboarding-guide">
          <Tooltip content="This is your task list! You can view and manage your tasks here.">
            <button className="onboarding-btn">Task List</button>
          </Tooltip>
          
          <Tooltip content="You can add a new task here! Just click this button.">
            <button className="onboarding-btn">Add Task</button>
          </Tooltip>

          <Tooltip content="Click on a task to edit or delete it." >
            <button className="onboarding-btn">Edit Task</button>
          </Tooltip>

          {/* Close button */}
          <button onClick={handleCloseGuide} className="close-btn">Close</button>
        </div>
      )}
    </>
  );
};

export default OnboardingGuide;
