import React, { useState, useEffect } from 'react';
import './App.css';
import { Container, Typography, Box, Button } from '@mui/material';
import { ThemeProvider } from '@mui/material/styles';
import { CssBaseline } from '@mui/material'; // Global reset
import { lightTheme, darkTheme } from './theme'; // Import both themes
import TaskList from './components/TaskList';
import OnboardingGuide from './components/OnboardingGuide';

function App() {
  // Check localStorage for the theme preference on component mount
  const savedTheme = localStorage.getItem('theme');
  const [isDarkMode, setIsDarkMode] = useState(savedTheme === 'dark');

  useEffect(() => {
    // Save theme preference in localStorage when it changes
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <CssBaseline />
      <Container maxWidth="md" sx={{ paddingTop: 4 }}>
        <Box textAlign="center" marginBottom={3}>
          <Typography variant="h3" component="h1">
            To-Do List Application
          </Typography>
        </Box>

        {/* Button to toggle between dark and light themes */}
        <Box textAlign="center" marginBottom={3}>
          <Button variant="contained" onClick={toggleTheme}>
            Switch to {isDarkMode ? 'Light' : 'Dark'} Mode
          </Button>
        </Box>

        {/* Onboarding guide component */}
        <OnboardingGuide />
        {/* Task list component */}
        <TaskList />
      </Container>
    </ThemeProvider>
  );
}

export default App;
