// src/theme.js
import { createTheme } from '@mui/material/styles';

// Light theme
const lightTheme = createTheme({
  palette: {
    mode: 'light', // Set light mode
    primary: {
      main: '#3f51b5', // Blue color for primary button and elements
    },
    secondary: {
      main: '#f50057', // Pink color for secondary elements
    },
    background: {
      default: '#f4f6f8', // Default background color for light theme
    },
    text: {
      primary: '#333333', // Primary text color
    },
  },
  typography: {
    fontFamily: '"Roboto", sans-serif',
    h1: {
      fontWeight: 600,
      fontSize: '2.5rem',
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.5,
    },
  },
});

// Dark theme
const darkTheme = createTheme({
  palette: {
    mode: 'dark', // Set dark mode
    primary: {
      main: '#90caf9', // Light blue color for primary elements
    },
    secondary: {
      main: '#f48fb1', // Light pink color for secondary elements
    },
    background: {
      default: '#121212', // Dark background color
    },
    text: {
      primary: '#e0e0e0', // Light text color for dark mode
    },
  },
  typography: {
    fontFamily: '"Roboto", sans-serif',
    h1: {
      fontWeight: 600,
      fontSize: '2.5rem',
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.5,
    },
  },
});

export { lightTheme, darkTheme };
