import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from 'moment';
import { AppBar, useMediaQuery, Grid, Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Typography, Card, CardContent, CardActions, Checkbox, FormControlLabel } from '@mui/material';

const TaskList = () => {

  const isMobile = useMediaQuery('(max-width:600px)');
  const [tasks, setTasks] = useState([]);
  const [open, setOpen] = useState(false); // State for dialog visibility
  const [editMode, setEditMode] = useState(false); // Flag to check if it's an edit mode
  const [isDeadlineChanged, setIsDeadlineChanged] = useState(false); // Track if deadline is changed
  const [currentTask, setCurrentTask] = useState({
    _id: '',
    title: '',
    description: '',
    deadline: '',
    completed: false,
  });

  // Validation errors
  const [errors, setErrors] = useState({
    title: '',
    description: '',
    deadline: '',
  });

  // States for delete dialog
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false); // For delete dialog visibility
  const [taskToDelete, setTaskToDelete] = useState(null); // Track the task to be deleted

  // Fetch tasks on component mount
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/tasks`)
      .then((response) => {
        const sortedTasks = response.data.sort((a, b) => {
          const deadlineA = new Date(a.deadline);
          const deadlineB = new Date(b.deadline);
          return deadlineA - deadlineB;
        });
        setTasks(sortedTasks);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'deadline') {
      setIsDeadlineChanged(true); // Mark that the deadline has been edited
    }

    setCurrentTask({
      ...currentTask,
      [name]: value,
    });
  };

  // Handle Completed checkbox change (Only in Edit mode)
  const handleCompletedChange = (e) => {
    setCurrentTask({ ...currentTask, completed: e.target.checked });
  };

  // Validate the title, description, and deadline
  const validateFields = () => {
    const { title, description, deadline } = currentTask;
    let formValid = true;
    const newErrors = { title: '', description: '', deadline: '' };

    // Title validation
    if (!title) {
      newErrors.title = 'Title is required';
      formValid = false;
    } else if (title.length < 5) {
      newErrors.title = 'Title must be at least 5 characters';
      formValid = false;
    } else if (title.length > 25) {
      newErrors.title = 'Title must not exceed 25 characters';
      formValid = false;
    } else if (title.trim() !== title) {
      newErrors.title = 'Title must not start or end with spaces';
      formValid = false;
    }

    // Description validation (optional)
    if (description && description.length > 200) {
      newErrors.description = 'Description can be at most 200 characters';
      formValid = false;
    }

    // Deadline validation (only if the deadline was changed)
    if (isDeadlineChanged && !deadline) {
      newErrors.deadline = 'Deadline is required';
      formValid = false;
    } else if (isDeadlineChanged) {
      const currentDate = new Date();
      const taskDeadline = new Date(deadline);
      if (taskDeadline <= currentDate) {
        newErrors.deadline = 'Deadline must be in the future';
        formValid = false;
      }
    }

    setErrors(newErrors);
    return formValid;
  };

  // Handle opening the dialog for adding a new task
  const handleOpenDialog = () => {
    setOpen(true);
    setEditMode(false); // Open in add mode
    setCurrentTask({
      _id: '',
      title: '',
      description: '',
      deadline: '',
      completed: false,
    });
    setErrors({ title: '', description: '', deadline: '' });
  };

  // Handle opening the dialog for editing a task
  const handleOpenEditDialog = (task) => {
    setOpen(true);
    setEditMode(true); // Open in edit mode

    // Format the deadline for 'datetime-local' input (YYYY-MM-DDTHH:mm)
    const formattedDeadline = task.deadline
      ? moment(task.deadline).format('YYYY-MM-DDTHH:mm')
      : '';

    setCurrentTask({
      _id: task._id,
      title: task.title,
      description: task.description,
      deadline: formattedDeadline,
      completed: task.completed,
    });
    setErrors({ title: '', description: '', deadline: '' });
    setIsDeadlineChanged(false); // Reset deadline change state when editing
  };

  const formatDate = (dateString) => {
    return moment(dateString).format('MM/DD/YYYY HH:mm');
  };

  // Handle closing the dialog
  const handleCloseDialog = () => {
    setOpen(false);
    setErrors({ title: '', description: '', deadline: '' }); // Reset errors
  };

  // Handle adding or editing a task
  const handleSubmit = (e) => {
    e.preventDefault();

    // Only validate if the deadline has been changed
    if (isDeadlineChanged) {
      const currentDate = new Date();
      const taskDeadline = new Date(currentTask.deadline);

      if (taskDeadline < currentDate) {
        setErrors({
          ...errors,
          deadline: 'Deadline must be in the future',
        });
        return;
      }
    }

    // Proceed with saving the task
    if (validateFields()) {
      const taskData = {
        title: currentTask.title,
        description: currentTask.description,
        deadline: currentTask.deadline,
        completed: currentTask.completed,
      };

      if (editMode) {
        // Update task
        axios
          .put(`${process.env.REACT_APP_API_URL}/tasks/${currentTask._id}`, taskData)
          .then((response) => {
            setTasks(
              tasks.map((task) =>
                task._id === currentTask._id ? { ...task, ...response.data } : task
              )
            );
            handleCloseDialog();
          })
          .catch((error) => {
            console.error('Error editing task:', error);
            alert('Failed to edit task');
          });
      } else {
        // Add new task
        axios
          .post(`${process.env.REACT_APP_API_URL}/tasks`, taskData)
          .then((response) => {
            setTasks([...tasks, response.data]);
            handleCloseDialog();
          })
          .catch((error) => {
            console.error('Error adding task:', error);
            alert('Failed to add task');
          });
      }
    }
  };

  // Handle task deletion
  const handleDelete = (taskId) => {
    axios
      .delete(`${process.env.REACT_APP_API_URL}/tasks/${taskId}`)
      .then(() => {
        setTasks(tasks.filter((task) => task._id !== taskId));
        setDeleteDialogOpen(false); // Close the delete confirmation dialog
      })
      .catch((error) => {
        console.error('Error deleting task:', error);
        alert('Failed to delete task');
        setDeleteDialogOpen(false); // Close the dialog in case of error
      });
  };

  // Open delete confirmation dialog
  const openDeleteDialog = (task) => {
    setTaskToDelete(task);
    setDeleteDialogOpen(true);
  };

  // Close delete confirmation dialog
  const closeDeleteDialog = () => {
    setDeleteDialogOpen(false);
    setTaskToDelete(null); // Reset task to delete
  };

  return (
    <Box sx={{  }}>
     <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', borderBottom : "" }}>
      <Typography variant="h5" gutterBottom>
        Tasks
      </Typography>

      {/* Button to open the "Add Task" dialog */}
      <Button variant="contained" color="primary" onClick={handleOpenDialog}>
        Add Task
      </Button>
    </Box>

      {/* Task Cards */}
      <Box sx={{ marginTop: '20px' }}>
      {/* Use Grid for responsive card layout */}
      <Grid container spacing={2}>
        {tasks.length === 0 ? (
          <Typography>No tasks available</Typography>
        ) : (
          tasks.map((task) => (
            <Grid item xs={12} sm={6} md={4}  lg={3} xl={2} key={task._id}>
              <Card sx={{ width: 250, boxShadow: 3, height: 170, display: 'flex', flexDirection: 'column' }}>
                <CardContent sx={{ flex: 1 }}>
                  <Typography
                    variant="h6"
                    style={{
                      textDecoration: task.completed ? 'line-through' : 'none',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                    }}
                    title={task.title} // Show full title on hover
                  >
                    {task.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    sx={{
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                    }}
                    title={task.description} // Show full description on hover
                  >
                    {task.description}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {formatDate(task.deadline)}
                  </Typography>
                  <Typography variant="body2" color={task.completed ? 'green' : 'red'}>
                    {task.completed ? 'Completed' : 'Pending'}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button
                    size="small"
                    color="primary"
                    onClick={() => handleOpenEditDialog(task)}
                  >
                    Edit
                  </Button>
                  <Button
                    size="small"
                    color="error"
                    onClick={() => openDeleteDialog(task)}
                  >
                    Delete
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Box>

      {/* Dialog for adding or editing a task */}
      <Dialog open={open} onClose={handleCloseDialog}>
        <DialogTitle>{editMode ? 'Edit Task' : 'Add New Task'}</DialogTitle>
        <DialogContent>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Title"
              name="title"
              value={currentTask.title}
              onChange={handleInputChange}
              fullWidth
              required
              margin="normal"
              error={!!errors.title}
              helperText={errors.title}
            />
            <TextField
              label="Description"
              name="description"
              value={currentTask.description}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
              error={!!errors.description}
              helperText={errors.description}
            />
            <TextField
              label="Deadline"
              name="deadline"
              type="datetime-local"
              value={currentTask.deadline}
              onChange={handleInputChange}
              fullWidth
              required
              margin="normal"
              InputLabelProps={{
                shrink: true, 
              }}
              error={!!errors.deadline}
              helperText={errors.deadline}
            />
            {editMode && (
              <FormControlLabel
                control={
                  <Checkbox
                    checked={currentTask.completed}
                    onChange={handleCompletedChange}
                    name="completed"
                    color="primary"
                  />
                }
                label="Completed"
              />
            )}
            <DialogActions>
              <Button onClick={handleCloseDialog} color="secondary">
                Cancel
              </Button>
              <Button type="submit" color="primary">
                {editMode ? 'Update Task' : 'Add Task'}
              </Button>
            </DialogActions>
          </form>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={deleteDialogOpen} onClose={closeDeleteDialog}>
        <DialogTitle>Confirm Deletion</DialogTitle>
        <DialogContent>
          <Typography>Are you sure you want to delete this task?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeDeleteDialog} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={() => {
              if (taskToDelete) handleDelete(taskToDelete._id);
            }}
            color="error"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TaskList;
