import React, { useState, useEffect } from 'react';
import { Button, Dialog, DialogActions, DialogContent, DialogTitle, Typography, Box } from '@mui/material';
import { Tooltip } from 'react-tooltip';


const OnboardingGuide = () => {
  const [open, setOpen] = useState(false); // Controls onboarding modal visibility
  const [step, setStep] = useState(0); // Step for the onboarding guide
  const [showTooltip, setShowTooltip] = useState(false); // Show tooltip for "Add Task" button

  useEffect(() => {
    const onboardingStatus = localStorage.getItem('onboardingStatus');
    if (!onboardingStatus) {
      // Show onboarding guide only if the user hasn't completed it before
      setOpen(true);
    }
  }, []);

  const handleClose = () => {
    setOpen(false);
    localStorage.setItem('onboardingStatus', 'completed');
  };

  const handleNextStep = () => {
    if(step ===2)
    {
        handleClose();
    }
    else if (step === 1) {
      setShowTooltip(true); // Show tooltip after step 1
    }
  };

  const handleSkip = () => {
    setOpen(false);
    localStorage.setItem('onboardingStatus', 'completed');
  };

  return (
    <Box sx={{ padding: '20px' }}>
      {/* Tooltip for Add Task Button */}
      <Tooltip
        title="Click to add a new task"
        placement="right"
        open={showTooltip}
      >
        <Button variant="contained" color="primary" onClick={() => console.log("Add Task clicked!")}>
          Add Task
        </Button>
      </Tooltip>

      {/* Onboarding Dialog */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Welcome to Your Task Manager!</DialogTitle>
        <DialogContent>
          {step === 0 && (
            <>
              <Typography variant="h6">Step 1: Introduction</Typography>
              <Typography>
                This app helps you manage your tasks and stay organized. You can add, edit, and delete tasks here.
              </Typography>
            </>
          )}
          {step === 1 && (
            <>
              <Typography variant="h6">Step 2: Adding a Task</Typography>
              <Typography>
                Click the "Add Task" button to create a new task. You can set a title, description, and deadline.
              </Typography>
            </>
          )}
          {step === 2 && (
            <>
              <Typography variant="h6">Step 3: Edit and Delete Tasks</Typography>
              <Typography>
                You can edit or delete tasks. Editing allows you to update the title, description, and deadline.
              </Typography>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleSkip} color="secondary">Skip</Button>
          <Button onClick={handleNextStep} color="primary">
            {step === 2 ? 'Finish' : 'Next'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default OnboardingGuide;
