// src/theme.js
import { createTheme } from '@mui/material/styles';

// GeeksforGeeks Blue Palette
const geeksBluePrimary = '#2196F3'; // Primary Blue (main actions)
const geeksBlueSecondary = '#0D47A1'; // Secondary Blue (hover, links)
const geeksBlueBackgroundLight = '#E3F2FD'; // Light Blue background for light mode
const geeksBlueBackgroundDark = '#121212'; // Dark background for dark mode
const geeksBlueTextPrimary = '#333333'; // Dark text for light mode
const geeksBlueTextSecondary = '#e0e0e0'; // Light text for dark mode

const lightTheme = createTheme({
  palette: {
    mode: 'light', // Light mode
    primary: {
      main: geeksBluePrimary, // Primary blue
    },
    secondary: {
      main: geeksBlueSecondary, // Secondary blue
    },
    background: {
      default: geeksBlueBackgroundLight, // Light blue background
    },
    text: {
      primary: geeksBlueTextPrimary, // Dark text color for light mode
    },
  },
  typography: {
    fontFamily: '"Roboto", sans-serif', // Use Roboto font
    h1: {
      fontWeight: 600,
      fontSize: '2.5rem',
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.5,
    },
  },
});

const darkTheme = createTheme({
  palette: {
    mode: 'dark', // Dark mode
    primary: {
      main: geeksBluePrimary, // Primary blue
    },
    secondary: {
      main: geeksBlueSecondary, // Secondary blue
    },
    background: {
      default: geeksBlueBackgroundDark, // Dark background for dark mode
    },
    text: {
      primary: geeksBlueTextSecondary, // Light text for dark mode
    },
  },
  typography: {
    fontFamily: '"Roboto", sans-serif', // Use Roboto font
    h1: {
      fontWeight: 600,
      fontSize: '2.5rem',
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.5,
    },
  },
});

export { lightTheme, darkTheme };
